#! /opt/homebrew/bin/python3
from itertools import islice, count

INPUT = ''

def first_distinct_n_characters(signal, n):
  for counter, *window in zip(count(n), *[islice(signal, i, None) for i in range(n)]):
    if len(set(window)) == n:
      return counter
  return -1

def process_part_1(signal):
  return first_distinct_n_characters(signal, 4)

def process_part_2(signal):
  return first_distinct_n_characters(signal, 14)

# Solution
print(process_part_1(INPUT))
print(process_part_2(INPUT))

# Tests
assert process_part_1('mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 7
assert process_part_1('bvwbjplbgvbhsrlpgdmjqwftvncz') == 5
assert process_part_1('nppdvjthqldpwncqszvftbrmjlhg') == 6
assert process_part_1('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg') == 10
assert process_part_1('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw') == 11

assert process_part_2('mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 19
assert process_part_2('bvwbjplbgvbhsrlpgdmjqwftvncz') == 23