#! /opt/homebrew/bin/python3
from itertools import islice, count

INPUT = ''

def process_part_1(signal):
  for counter, *window in zip(count(4), iter(signal), islice(signal, 1, None), islice(signal, 2, None), islice(signal, 3, None)):
    if len(set(window)) == 4:
      return counter
  return -1

# Solution
print(process_part_1(INPUT))

# Tests
assert process_part_1('mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 7
assert process_part_1('bvwbjplbgvbhsrlpgdmjqwftvncz') == 5
assert process_part_1('nppdvjthqldpwncqszvftbrmjlhg') == 6
assert process_part_1('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg') == 10
assert process_part_1('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw') == 11